<?php

$inputs = '



';

$inputLine = explode(PHP_EOL, $inputs);
$tempX = $tempY = $x = $y = 2;
foreach ($inputLine as $v) {
	$instructions = str_split($v);
	foreach ($instructions as $ins) {
		$tempX = ($ins == 'L') ? --$tempX : ($ins == 'R' ? ++$tempX : $tempX);
		$x = ($tempX > 3 || $tempX < 1) ? $x : $tempX;
		$tempY = ($ins == 'U') ? --$tempY : ($ins == 'D' ? ++$tempY : $tempY);
		$y = ($tempY > 3 || $tempY < 1) ? $y : $tempY;
		$tempX = $x; $tempY = $y;
	}
	echo $x . $y . PHP_EOL;
}